# Write your MySQL query statement below
select DATE_FORMAT(t.trans_date, "%Y-%m") as month, country, count(*) as trans_count, SUM(if(state = "approved", 1, 0)) as approved_count, SUM(t.amount) as trans_total_amount, SUM(if(state = "approved", amount, 0)) as approved_total_amount from Transactions t group by month, country;