# Write your MySQL query statement below
select st.student_id, st.student_name, sub.subject_name, count(exm.student_id) as "attended_exams" from Students st CROSS JOIN Subjects sub LEFT JOIN Examinations exm ON st.student_id = exm.student_id and sub.subject_name = exm.subject_name group by st.student_id, st.student_name, sub.subject_name order by st.student_id, sub.subject_name;